/*
This stylesheet defines a simple layout with the sidebar at right.
*/
article,
#primary {
  margin: 0 1% 3em 1%;
  float: left;
  width: 67%;
}

aside,
#secondary {
  margin: 0 1% 3em 1%;
  float: right;
  width: 28%;
}

footer {
  clear:both;
  text-align: center;
  padding: 1em;
}

@media print {
  article,
  #primary {
    width: 100%;
    float: none;
    margin: 0;
  }

  aside,
  #secondary,
  footer {
    display: none;
  }
}
